var didTestPass = function (expectedResult, result) {
    if (typeof expectedResult == "object") {
        console.log(arrayEquals(expectedResult, result)
            ? "Passed ✔️"
            : "Failed ❌. Expected " +
                expectedResult +
                " but instead, got " +
                result);
    }
    else {
        console.log(expectedResult === result
            ? "Passed ✔️"
            : "Failed ❌ Expected " + expectedResult + " but instead, got " + result);
    }
};
var arrayEquals = function (arr1, arr2) {
    if (arr1.length !== arr2.length)
        return false;
    for (var i = 0; i < arr1.length; i++) {
        if (arr1[i] !== arr2[i]) {
            return false;
        }
    }
    return true;
};
var header = function (name, date) {
    console.log("-------- Daily Coding Problem --------\n\n");
    console.log("-------- " + name + " " + date + " --------\n\n");
    console.log("-------- Good Luck!! --------\n\n");
};
module.exports = {
    didTestPass: didTestPass,
    arrayEquals: arrayEquals,
    header: header
};
