// @author Liam Neville
var didTestPass = function (expectedResult, result) {
    if (typeof expectedResult == "object") {
        console.log(arrayEquals(expectedResult, result) ? "Passed ✔️" : "Failed ❌. Expected " + expectedResult + " but instead, got " + result);
    }
    else {
        console.log(expectedResult === result ? "Passed ✔️" : "Failed ❌. Expected " + expectedResult + " but instead, got " + result);
    }
};
var arrayEquals = function (arr1, arr2) {
    if (arr1.length !== arr2.length)
        return false;
    arr1.forEach(function (element, index) {
        if (element !== arr2[index])
            return false;
    });
    return true;
};
module.exports = {
    didTestPass: didTestPass,
    arrayEquals: arrayEquals
};
