const didTestPass = function (expectedResult: any, result: any) :void {
    if (typeof expectedResult == "object") {
        console.log(arrayEquals(expectedResult, result) ? "Passed ✔️" : "Failed ❌. Expected " + expectedResult + " but instead, got " + result);
    } else {
        console.log(expectedResult === result ? "Passed ✔️" : "Failed ❌. Expected " + expectedResult + " but instead, got " + result);
    }
};

const arrayEquals = function (arr1: Array<any>, arr2: Array<any>) : boolean {
    
    if (arr1.length !== arr2.length) return false;

    arr1.forEach((element: any, index: number) => {
        if (element !== arr2[index]) return false;
    })
    return true;
};

module.exports = {
    didTestPass,
    arrayEquals
}

