const didTestPass = function(expectedResult: any, result: any): void {
  if (typeof expectedResult == "object") {
    console.log(
      arrayEquals(expectedResult, result)
        ? "Passed ✔️"
        : "Failed ❌. Expected " +
            expectedResult +
            " but instead, got " +
            result
    );
  } else {
    console.log(
      expectedResult === result
        ? "Passed ✔️"
        : "Failed ❌ Expected " + expectedResult + " but instead, got " + result
    );
  }
};

const arrayEquals = function(arr1: Array<any>, arr2: Array<any>): boolean {
  if (arr1.length !== arr2.length) return false;

  for (let i : number = 0; i < arr1.length; i++) {
    if (arr1[i] !== arr2[i]) {
      return false;
    }
  }
  return true;
};

const header = function(name: string, date: string): void {
  console.log("-------- Daily Coding Problem --------\n\n");
  console.log("-------- " + name + " " + date + " --------\n\n");
  console.log("-------- Good Luck!! --------\n\n");
};

module.exports = {
  didTestPass,
  arrayEquals,
  header
};
